import axios from "axios"

// Get the API base URL from environment or default to the correct port
const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5009/api"

console.log("API Base URL:", API_BASE_URL)

// Create axios instance with base configuration
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: {
    "Content-Type": "application/json",
  },
})

// Add request interceptor to include auth token and log requests
api.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method?.toUpperCase()} request to:`, config.baseURL + config.url)
    console.log("Request data:", config.data)

    const token = localStorage.getItem("token")
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
      console.log("Added auth token to request")
    }
    return config
  },
  (error) => {
    console.error("Request interceptor error:", error)
    return Promise.reject(error)
  },
)

// Add response interceptor to handle errors and log responses
api.interceptors.response.use(
  (response) => {
    console.log("API Response:", {
      status: response.status,
      url: response.config.url,
      data: response.data,
    })
    return response
  },
  (error) => {
    console.error("API Error:", {
      status: error.response?.status,
      statusText: error.response?.statusText,
      data: error.response?.data,
      message: error.message,
      url: error.config?.url,
      fullUrl: error.config?.baseURL + error.config?.url,
    })

    if (error.response?.status === 401) {
      console.log("Unauthorized - clearing auth data")
      localStorage.removeItem("token")
      localStorage.removeItem("user")
    }
    return Promise.reject(error)
  },
)

export default api
